                    writer.WriteNumber("order", pokemonDetail.OrderOption.Value!.Value);
                else
                    writer.WriteNull("order");

            if (pokemonDetail.WeightOption.IsSet)
                if (pokemonDetail.WeightOption.Value != null)
                    writer.WriteNumber("weight", pokemonDetail.WeightOption.Value!.Value);
                else
                    writer.WriteNull("weight");
        }
    }
}
